function SettingsCache(){this.address="../../admin/modules/settings/cache/"}SettingsCache.prototype.init=function(){$("#cacheActive").on("change",function(){$(this).is(":checked")?$("#cacheSettings").show():$("#cacheSettings").hide()});$("#settings_cache_save").click(function(){settingsCache.cacheSave()});$("#no_cache_submit").click(function(){settingsCache.addNoCache()});$("#nonCacheList tr").click(function(){var a=$(this);settingsCache.deleteNoCache(a)})};
SettingsCache.prototype.cacheSave=function(){if(validation.html5Validation("expire")){var a={cache:0,expire:$("#expire").val()};$("#cacheActive").is(":checked")&&(a.cache=1);$.post(settingsCache.address+"cache",a);$("#notice").addClass("notice").html(languageAdmin.admin_settings_saved)}};
SettingsCache.prototype.addNoCache=function(){var a=$.trim($("#noCachePage").val());""!=a&&($("#noCachePage").val(""),$.post(settingsCache.address+"addNoCache",{page:a},function(a){a=JSON.parse(a);-1!=a.id&&$("#nonCacheList").append('<tr data-id="'+a.id+'">   <td><img src="'+a.style_dir+'images/icons/delete.png" alt="'+a.deleteText+'" title="'+a.deleteText+'"></td>  <td>'+a.name+"</td> </tr>")}))};
SettingsCache.prototype.deleteNoCache=function(a){var b=a.data("id");confirmBox.init(350,function(){a.remove();$.post(settingsCache.address+"deleteNoCache",{id:b})});confirmbox.show("Cache pagina verwijderen",languageAdmin.cache_cache_again)};var settingsCache=new SettingsCache;